document.addEventListener("DOMContentLoaded",(()=>{const e=document.getElementById("grid-container");for(let t=0;t<100;t++){const n=document.createElement("div");n.className="grid-item";n.dataset.index=t;e.appendChild(n)}}));function placeItem(){const e=parseInt(document.getElementById("x-coord").value);const t=parseInt(document.getElementById("y-coord").value);const n=document.getElementById("image-upload").files[0];const o=document.getElementById("grid-container");if(e>=0&&t>=0&&e<10&&t<10&&n){const d=t*10+e;const c=o.querySelector(`.grid-item[data-index='${d}']`);const r=document.createElement("img");r.src=URL.createObjectURL(n);r.width=32;r.height=32;r.style.objectFit="cover";c.innerHTML="";c.appendChild(r)}else{alert("Coordinates are out of bounds or no image selected")}}function downloadGrid(){const e=document.getElementById("grid-container");const t=e.querySelectorAll(".grid-item");t.forEach((e=>{e.style.border="none"}));html2canvas(e).then((e=>{const n=document.createElement("a");n.download="grid.png";n.href=e.toDataURL();n.click();t.forEach((e=>{e.style.border="1px solid #ccc"}))}))}