document.getElementById("resize-button").addEventListener("click",(function(){const e=document.getElementById("upload").files[0];const t=document.getElementById("width").value;const n=document.getElementById("height").value;const o=document.getElementById("canvas");const d=o.getContext("2d");const a=document.getElementById("download");if(e&&t&&n){const c=new Image;const l=new FileReader;l.onload=function(e){c.src=e.target.result;c.onload=function(){const e=c.width;const l=c.height;let s=t;let i=n;const g=e/l;if(t/n>g){s=n*g}else{i=t/g}o.width=s;o.height=i;d.drawImage(c,0,0,s,i);a.href=o.toDataURL();o.style.display="block"}};l.readAsDataURL(e)}else{alert("Please upload an image and specify width and height.")}}));